import './App.css';
import React,{ Component } from 'react'
import { Map } from 'react-amap';

export default class App extends Component {
  constructor(props) {
    super(props);
  }

  componentDidMount() {
    let disCountry = new window.AMap.DistrictLayer.Country({
        zIndex:10,
        SOC:'CHN',
        depth:2,
        styles:{
            'nation-stroke':'transparent',
            'coastline-stroke':[0.85, 0.63, 0.94, 0],
            'province-stroke':'red',
            'city-stroke': 'rgba(255,255,255,0.15)',//中国特有字段
            // 'fill':function(props){//中国特有字段
            //   return getColorByDGP(props.adcode_pro)
            // }
        }
    })
    //eslint-disable-next-line 
    this.map = new window.AMap.Map('map', {
        center: [113.280637, 23.125178],
        resizeEnable: true,
        zoom: 8,
        layers: [
          disCountry
        ]
    })

    //eslint-disable-next-line 
    window.AMap.plugin('AMap.DistrictSearch', () => {
        //eslint-disable-next-line 
        let districtSearch = new window.AMap.DistrictSearch({
        // 关键字对应的行政区级别，共有5种级别
        level: 'country',
        //  是否显示下级行政区级数，1表示返回下一级行政区
        subdistrict: 0,
       // 返回行政区边界坐标点
        extensions: 'all',
      })

        // 搜索所有省/直辖市信息
        districtSearch.search(100000, (status, result) => {
          // 查询成功时，result即为对应的行政区信息
          this.handlePolygon(result);
        })

      //   districtSearch.search('中国',(status,result) => {
      //     // 外多边形坐标数组和内多边形坐标数组
      //     var outer = [
      //         new window.AMap.LngLat(-360,90,true),
      //         new window.AMap.LngLat(-360,-90,true),
      //         new window.AMap.LngLat(360,-90,true),
      //         new window.AMap.LngLat(360,90,true),
      //     ];
      //     var holes = result.districtList[0].boundaries

      //     var pathArray = [
      //         outer
      //     ];
      //     pathArray.push.apply(pathArray,holes)
      //     var polygon = new window.AMap.Polygon( {
      //         pathL:pathArray,
      //         strokeColor: '#00eeff',
      //         strokeWeight: 1,
      //         fillColor: '#71B3ff',
      //         fillOpacity: 1
      //     });
      //     polygon.setPath(pathArray);
      //     this.map.add(polygon);
      //     this.map.setFitView();
      // })
    })
  }

  handlePolygon(result) {
    let bounds = result.districtList[1].boundaries
    let newBounds = [];
    bounds.map((v, k) => {
      let a;
      if (k == 0) {
        console.log(v);
      }

      a = v.filter((element, index, array) => {
        return index % 100 == 0;
      })

      if (k == 0) {
        console.log(a);
      }

      newBounds.push(a);

    })


    if (newBounds) {
      for (let i = 0, l = newBounds.length; i < l; i++) {
        //生成行政区划polygon
        //eslint-disable-next-line 
        let polygon = new window.AMap.Polygon({
          map: this.map,    // 指定地图对象
          strokeWeight: 2,    // 轮廓线宽度
          path: newBounds[i],     //轮廓线的节点坐标数组
          fillOpacity: 0.15,     //透明度
          fillColor: '#256edc',     //填充颜色
          strokeColor: '#256edc',    //线条颜色
        })

        polygon.on('click', (e) => {
        // 点击绘制的区域时执行其他交互

        })
      }
      // 地图自适应
      this.map.setFitView()
     }
  }

  render() {
    return (
      <div className="container">
        <div id="map"></div>
      </div>
    )
  }
}
